<?php

namespace App\Console\Commands;

use App\Models\BlogPost;
use Illuminate\Console\Command;
use Illuminate\Support\Str;
use Carbon\Carbon;

class FetchMedicalArticles extends Command
{
	/**
	 * The name and signature of the console command.
	 *
	 * @var string
	 */
	protected $signature = 'blog:fetch-articles';

	/**
	 * The console command description.
	 *
	 * @var string
	 */
	protected $description = 'Fetch medical articles from the internet and store them in the database';

	/**
	 * Execute the console command.
	 */
	public function handle(): int
	{
		$this->info('Fetching medical articles...');

		$articles = $this->getMedicalArticles();

		$imported = 0;
		$bar = $this->output->createProgressBar(count($articles));
		$bar->start();

		foreach ($articles as $article) {
			$slug = Str::slug($article['title']);
			
			BlogPost::updateOrCreate(
				['slug' => $slug],
				[
					'title' => $article['title'],
					'excerpt' => $article['excerpt'],
					'content' => $article['content'],
					'image_url' => $article['image_url'],
					'author' => $article['author'],
					'category' => $article['category'],
					'read_time' => $article['read_time'],
					'published_at' => Carbon::now()->subDays(rand(0, 30)),
				]
			);
			$imported++;
			$bar->advance();
		}

		$bar->finish();
		$this->newLine();
		$this->info("Successfully imported/updated {$imported} medical articles.");

		return self::SUCCESS;
	}

